import java.awt.Color;import java.awt.Font;import java.awt.Graphics;/*******************************************************************/** Role du fichier   : Dessiner mathematiquement l'objet PlateauRessources/** Ateur             : Fabrice Lecomte        					/** Date de creation  : 29/04/2014  /**     Modifications : 15/06/2014 Modification des couleurs       					/*********************************************************************/  class PlateauRessources {	private int [][] Plateau;private static Color marronfonce = new Color( 80, 40, 0 );private final static Color MentheAlEau = new Color(22,249,141);int Pointy;int k;String texte = "";private int Bois ;private int Argile ;private int Roseau ;private int Pierre ;private int Cereale ;private int Legume ;private int Mouton ;private int Sanglier ;private int Boeuf ;private int PointNourriture; private int PointMendicite;public PlateauRessources(int b, int a, int r, int p, int c, int l, int m, int s, int bo, int pn, int pm){	 Bois = b;	 Argile = a;	 Roseau = r;	 Pierre = p;	 Cereale = c;	 Legume = l;	 Mouton = m;	 Sanglier = s;	 Boeuf = bo;	 PointNourriture = pn; 	 PointMendicite = pm;}public void dessine(Graphics g) {	Font maFonte = new Font("Courier", Font.BOLD, 15);	g.setFont(maFonte);	k = 0;	for (int i = 0; i < 11; i++) {		Pointy = (i*50)+10;		k = k+1;        new Rectangle(10, Pointy, 250, 45, g, marronfonce);    	    Pointy = Pointy + 25 ;	    g.setColor(MentheAlEau);        switch (k) {        case 1 :         	texte = "Bois                : "+Bois;        	break;        case 2 :         	texte = "Argile              : "+Argile;        	break;        case 3 :         	texte = "Roseau              : "+Roseau;        	break;        case 4 :         	texte = "Pierre              : "+Pierre;        	break;        case 5 :         	texte = "Cereale             : "+Cereale;        	break;        case 6 :         	texte = "Legume              : "+Legume;        	break;        case 7 :         	texte = "Mouton              : "+Mouton;        	break;        case 8 :         	texte = "Sanglier            : "+Sanglier;        	break;        case 9 :         	texte = "Boeuf               : "+Boeuf;        	break;        case 10 :         	texte = "Point de nourriture : "+PointNourriture;        	break;        case 11 :         	texte = "Point de mendicite  : "+PointMendicite;        	break;        }     	g.drawString( texte, 20 ,Pointy) ;        }      } }	